mapper.select:
SELECT
FROM strategy_info WHERE status = 1
AND strategy_type = #{strategyType}
AND (strategy_code LIKE CONCAT('%', #{keyword}, '%') OR strategy_name LIKE CONCAT('%', #{keyword}, '%'))
ORDER BY created_at DESC LIMIT #{offset}, #{limit}

SELECT COUNT(*) FROM strategy_info WHERE status = 1
AND strategy_type = #{strategyType}
AND (strategy_code LIKE CONCAT('%', #{keyword}, '%') OR strategy_name LIKE CONCAT('%', #{keyword}, '%'))

mapper.insert:
INSERT INTO strategy_info (
    strategy_code, strategy_name, strategy_type, risk_level, target_return,
    max_drawdown, rebalance_frequency, description, status
) VALUES (
    #{strategyCode}, #{strategyName}, #{strategyType}, #{riskLevel}, #{targetReturn},
    #{maxDrawdown}, #{rebalanceFrequency}, #{description}, #{status}
)

mapper.update:
UPDATE strategy_info SET
strategy_name = #{strategyName}, strategy_type = #{strategyType}, risk_level = #{riskLevel}, target_return = #{targetReturn}, max_drawdown = #{maxDrawdown}, rebalance_frequency = #{rebalanceFrequency}, description = #{description}, status = #{status},
WHERE id = #{id}
